package com.kasuya.helloimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport com.kasuya.hello.databinding.ActivityMainBindingimport android.view.Viewimport android.widget.TextViewimport android.widget.Toastimport java.util.Dateimport android.util.Logclass MainActivity : AppCompatActivity() {    private lateinit var binding: ActivityMainBinding    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = ActivityMainBinding.inflate(layoutInflater)        setContentView(binding.root)        binding.btnCurrent.setOnClickListener {            binding.textResult.text = Date().toString()//            val toast = Toast.makeText(this, Date().toString(), Toast.LENGTH_LONG)//            toast.show()            Log.d("CurrentTimg", Date().toString())        }    }    override fun onSaveInstanceState(outState: Bundle) {        super.onSaveInstanceState(outState)        val txtResult = findViewById<TextView>(R.id.textResult)        outState.putString("txtResult", txtResult.text.toString())    }    override fun onRestoreInstanceState(savedInstanceState: Bundle) {        super.onRestoreInstanceState(savedInstanceState)        val txtResult = findViewById<TextView>(R.id.textResult)        txtResult.text = savedInstanceState.getString("txtResult")    }}